function limpiar(texto) {
  if (texto === undefined || texto === null) return '';
  return String(texto)
    .normalize("NFD").replace(/[\u0300-\u036f]/g, "")
    .replace(/[^\w\s.,-]/gi, '')
    .trim();
}

function extraerValorMonetario(texto) {
  // Elimina el s√≠mbolo de d√≥lar y espacios, luego limpia el texto
  return limpiar(texto.replace(/\$/g, ''));
}

let rawOutput = $input.first().json.output || '';
let conversationalMessage = rawOutput.replace(/\\n/g, '\n');

// Primero intentar extraer JSON
let jsonMatch = conversationalMessage.match(/\{[\s\S]*\}/);
let pedido_json_data = null;
let error = null;

if (jsonMatch) {
  try {
    pedido_json_data = JSON.parse(jsonMatch[0]);
    conversationalMessage = conversationalMessage.replace(jsonMatch[0], '').trim();
    if (pedido_json_data.pedido_json) {
      pedido_json_data = pedido_json_data.pedido_json;
    }
  } catch (e) {
    error = 'Error parseando JSON: ' + e.message;
  }
}

let productos = [];
if (pedido_json_data && Array.isArray(pedido_json_data.items)) {
  productos = pedido_json_data.items;
} else if (pedido_json_data && Array.isArray(pedido_json_data.productos)) {
  productos = pedido_json_data.productos;
} else if (pedido_json_data && pedido_json_data.producto) {
  productos = [pedido_json_data];
}

// Si no se encontr√≥ JSON, extraer del mensaje formateado
if (productos.length === 0) {
  // Ajustada la regex para ser m√°s flexible con los espacios y saltos de l√≠nea.
  // Usamos \s+ para cualquier tipo de espacio (incluyendo saltos de l√≠nea) de uno o m√°s.
  const regexProductos = /‚Ä¢ (.*?)\s+-\s*Cantidad:\s*(\d+)\s+-\s*Precio unitario:\s*\$([\d.,]+)\s+-\s*Subtotal:\s*\$([\d.,]+)/g;
  let match;

  while ((match = regexProductos.exec(conversationalMessage)) !== null) {
    productos.push({
      producto: match[1].trim(), 
      cantidad: parseInt(match[2]),
      unitario: extraerValorMonetario(match[3]), 
      subtotal: extraerValorMonetario(match[4]) 
    });
  }
}

// Extraer direcci√≥n y horario del mensaje
const direccionMatch = conversationalMessage.match(/üìç \*Direcci√≥n:\* (.*?)(?:\n|$)/);
const horarioMatch = conversationalMessage.match(/‚è∞ \*Horario:\* (.*?)(?:\n|$)/);
const totalMatch = conversationalMessage.match(/üí∞ \*Total del pedido:\* \$([\d.,]+)/);

const direccion = limpiar(direccionMatch ? direccionMatch[1] : '');
const horario = limpiar(horarioMatch ? horarioMatch[1] : '');
const totalPedido = totalMatch ? extraerValorMonetario(totalMatch[1]) : ''; 
const cliente = limpiar($input.first().json.Cliente || $input.first().json.message?.from?.first_name || 'Cliente Gen√©rico');

if (productos.length === 0 && !error) {
  error = 'No se encontraron productos v√°lidos.';
}

// Mapear cada producto a un elemento separado para Google Sheets
const itemsParaGoogleSheets = productos.map(prod => ({
  json: {
    Producto: limpiar(prod.nombre || prod.producto || ''),
    Cantidad: limpiar(prod.cantidad || ''),
    PrecioUnitario: limpiar(prod.precio_unitario || prod.unitario || ''),
    Subtotal: limpiar(prod.subtotal || ''),
    Direcci√≥n: direccion,
    Horario: horario,
    Cliente: cliente,
    "Total Pedido": totalPedido 
  }
}));

// Si no hay productos, devolver un elemento con el error.
if (itemsParaGoogleSheets.length === 0 && error) {
  return [{
    json: {
      error: error,
      conversationalMessage: conversationalMessage
    }
  }];
} else if (itemsParaGoogleSheets.length === 0) {
  return [{
    json: {
      error: 'No se encontraron productos v√°lidos para Google Sheets.',
      conversationalMessage: conversationalMessage
    }
  }];
}

return itemsParaGoogleSheets;

