let allProducts = $input.all();

let emailMessageContent = '';
let totalOrderAmount = '';
let deliveryAddress = '';
let deliveryTime = '';
let customerName = '';

if (allProducts.length > 0) {
  const firstProduct = allProducts[0].json;
  deliveryAddress = limpiar(firstProduct.Direcci√≥n);
