  totalOrderAmount = limpiar(firstProduct["Total Pedido"]);
  customerName = limpiar(firstProduct.Cliente);

  let productsListHtml = '';
  allProducts.forEach(prodItem => {
    const product = prodItem.json;
    productsListHtml += `
      ‚Ä¢ <strong>${limpiar(product.Producto)}</strong><br>
        - Cantidad: ${limpiar(product.Cantidad)}<br>
        - Precio unitario: $${limpiar(product.PrecioUnitario)}<br>
        - Subtotal: $${limpiar(product.Subtotal)}<br><br>
    `;
  });

  emailMessageContent = `
    üì¶ <strong>Pedido confirmado</strong><br><br>
    ¬°Hola ${customerName || 'cliente'}!<br><br>
    Tu pedido ha sido confirmado y en breve lo estaremos procesando.<br><br>
    üìç <strong>Direcci√≥n:</strong> ${deliveryAddress}<br>
    ‚è∞ <strong>Horario:</strong> ${deliveryTime}<br><br>
    <strong>Detalles del pedido:</strong><br>
    ${productsListHtml}
    üíµ <strong>Total del pedido:</strong> $${$input.first().json.Total}
  `;
} else {
  emailMessageContent = `
    üì¶ <strong>Pedido confirmado</strong><br><br>
    Tu pedido ha sido confirmado, pero no se encontraron detalles de productos.<br><br>
    Por favor, contacta con nosotros para m√°s informaci√≥n.
  `;
